-- create "financial_accounts" table
CREATE TABLE "financial_accounts" ("id" character varying NOT NULL, "is_active" boolean NOT NULL, "version" integer NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "display_name" character varying NOT NULL, "bank_name" character varying NULL, "account_type" character varying NOT NULL, "balance" double precision NOT NULL, "currency_code" character varying NOT NULL, "user_financial_accounts" character varying NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "financial_accounts_users_financial_accounts" FOREIGN KEY ("user_financial_accounts") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
