-- create "cards" table
CREATE TABLE "cards" ("id" character varying NOT NULL, "is_active" boolean NOT NULL, "version" integer NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "display_name" character varying NOT NULL, "bank_name" character varying NULL, "last_digits" smallint NULL, "balance" double precision NOT NULL, "balance_limit" double precision NOT NULL, "currency_code" character varying NOT NULL, "card_type" character varying NOT NULL, "financial_account_id" character varying NULL, "user_cards" character varying NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "cards_financial_accounts_cards" FOREIGN KEY ("financial_account_id") REFERENCES "financial_accounts" ("id") ON UPDATE NO ACTION ON DELETE SET NULL, CONSTRAINT "cards_users_cards" FOREIGN KEY ("user_cards") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
